class Solution {
public:
    int uniquePathsWithObstacles(vector<vector<int> > &obstacleGrid) {
        if( obstacleGrid[0][0] ) return 0;
        int m = obstacleGrid.size();
        int n = obstacleGrid[0].size();
        int f[m][n];
        memset(f,0,sizeof(int) * m * n );
        int i,j;
        bool find1 = false;
        for ( i = 0; i < m; ++i)
        {
            f[i][0] = obstacleGrid[i][0] == 1 ? 0 : 1;
            if ( obstacleGrid[i][0] == 1 ) find1 = true;
            if( find1 ) f[i][0] = 0;
        }
        bool find2 = false;
        for ( i = 0; i < n; ++i)
        {
            f[0][i] = obstacleGrid[0][i] == 1 ? 0 : 1;
            if ( obstacleGrid[0][i] == 1 ) find2 = true;
            if( find2 ) f[0][i] = 0;
        }
        if ( (m == 1 || n == 1 ) && (find1 || find2) ) return 0;

        for ( i = 1; i < m; ++i)
        {
            for (j = 1; j < n; ++j)
            {
                if ( obstacleGrid[i][j] == 1 )
                {
                    f[i][j] = 0;
                }
                else
                {
                    f[i][j] = f[i-1][j] + f[i][j-1];
                }
            }
        }
        return f[m-1][n-1];
    }
};
int main(int argc, char const *argv[])
{
    vector<vector<int> > obstacleGrid;
    for (int i = 0; i < 3; ++i)
    {
        vector<int> tmp;
        for (int j = 0; j < 2; ++j)
        {
            tmp.push_back(0);
        }
        obstacleGrid.push_back(tmp);
    }
    obstacleGrid[1][0] = 1;
    obstacleGrid[1][1] = 1;

	Solution s;
	int result = s.uniquePathsWithObstacles( obstacleGrid );
	printf("%d\n", result);
	return 0;
}